#include<iostream>#include <string.h>#include "Set.cpp"#include<vector>using namespace std;vector <pair<int,int>> v[10];  //array ka index is the vertex pair x,y is index--x si the edge and y is its weightint dist[10];int vis[10];int nodes,edges;// Set<pair<int,int>> s;void shortest_path(){memset(vis,0, sizeof vis);dist[1]=0;Set<pair<int, int>> s;  //pair x,y where x is the weight and y is the destinations.insert({0,1});while(!s.empty()){	pair <int,int> p =s.begin();	s.erase(s.begin());	int x = p.second;	int wei = p.first;	if(vis[x]==1) continue;	for(int i=0;i<v[x].size();i++){		int e=v[x][i].first;		int w=v[x][i].second;		if(dist[x]+w<dist[e]){			dist[e]=dist[x]+w;			// cout<<"inserting: "<<dist[e]<<","<<e<<endl;			s.insert({dist[e],e});			// cout<<"inserted!"<<endl;		}	}}}void intialize(){	for(int i=1;i<=nodes;i++){		dist[i]=1234;	}}int main(){cin>>nodes>>edges;for(int i=1;i<=nodes;i++){	int x,y,z;	cin>>x>>y>>z;	v[x].push_back({y,z});}intialize();shortest_path();for(int i=2;i<=nodes;i++){	cout<<dist[i]<<" ";}return 0;}